declare:
//  --------------------------------------------------------------------------
//  Subscription object

typedef struct {
    client_t *client;           //  Always refers to live client
    char *path;                 //  Path client is subscribed to
} sub_t;


//  --------------------------------------------------------------------------
//  Constructor

static sub_t *
sub_new (client_t *client, char *path)
{
    sub_t *self = (sub_t *) zmalloc (sizeof (sub_t));
    self->client = client;
    self->path = strdup (path);
    return self;
}


//  --------------------------------------------------------------------------
//  Destructor

static void
sub_destroy (sub_t **self_p)
{
    assert (self_p);
    if (*self_p) {
        sub_t *self = *self_p;
        free (self->path);
        free (self);
        *self_p = NULL;
    }
}
